function Polaroid({
  image,
  caption,
  props,
}: {
  image: string;
  caption: string;
  props?: string;
}) {
  return (
    <div
      className={`hover:scale-105 transition transition-duration-300 bg-white w-full h-max p-3 z-40 ${props}`}
    >
      <img className="object-cover" src={image} />
      <p className="text-center">{caption}</p>
    </div>
  );
}

